var fs = require('fs')
var objects = require('./objects')

var cssnum = 0

function UseCSS(document, cssname) {
    if (fs.existsSync(cssname)) {
        var stylesheet = new objects.JSLinkObject(`./css${cssnum}.css`, 'stylesheet')
        document.AddHeaderObject(stylesheet)
        document.setresponsehandler(function() {
            document.GetRawResponse().sendFile(`${process.cwd()}/${cssname}`)
        }, `/css${cssnum}.css`)
        cssnum += 1
    }
}

module.exports = UseCSS